var grEvidenceControl = new GlideRecord('x_anbib_grc_m2m_sop_evidences_controls');
grEvidenceControl.addQuery("sop_evidence", current.sys_id.toString());
grEvidenceControl.query();

var wf = new global.Workflow();

while (grEvidenceControl.next()) {
	var grControl = new GlideRecord('x_anbib_grc_control');
	grControl.get(grEvidenceControl.control.toString());
	
	var contexts = wf.getRunningFlows(grControl);
	if (contexts.next()) {
		wf.broadcastEvent(contexts.sys_id.toString(), "EVIDENCE_FINISH");
	}
}

