workflow.scratchpad.children = [];
var entity = current.sys_id.toString();
var type = current.type.toString();
// Verifica se foi update e se sim, se teve alteração de tipo e layer

// If type cheanged, disable controls
if (type && workflow.variables.u_old_type && type != workflow.variables.u_old_type) {
	// Insert control
	var grOldControl = new GlideRecord("x_anbib_grc_control");
	grOldControl.addQuery("entity", entity);
	grOldControl.addQuery("layer.entity_type", workflow.variables.u_old_type);
	grOldControl.state = "8";  // disabled - type changed
	grOldControl.active = false;
	grOldControl.updateMultiple();	
}

updateEntityControls(entity, type);

function updateEntityControls(entity, type) {
	var grEntityLayer = new GlideRecord("x_anbib_grc_m2m_entities_layer_controls");
	grEntityLayer.addQuery("entity", entity);
	grEntityLayer.query();
	while (grEntityLayer.next()) {
		var layer = grEntityLayer.layer_control.toString();

		var grLayerControlTpl = new GlideRecord("x_anbib_grc_m2m_control_temp_layer_contro");
		grLayerControlTpl.addQuery("layer_control", layer);
		grLayerControlTpl.query();
		while (grLayerControlTpl.next()) {
			var template = grLayerControlTpl.control_template.toString();

			// Insert control
			var grControl = new GlideRecord("x_anbib_grc_control");
			grControl.addQuery("entity", entity);
			grControl.addQuery("control", template);
			grControl.addQuery("layer", layer);
			grControl.addQuery("layer.entity_type", type);
			grControl.query();

			if (!grControl.next()) {
				// Add control definition to array
// 				workflow.scratchpad.controls.push({
// 					u_entity: entity,
// 					u_control: template,
// 					u_layer: layer
// 				});
				grControl.initialize();
				grControl.entity = entity;
				grControl.control = template;
				grControl.layer = layer;
				grControl.state = "0";
				grControl.frequency = grLayerControlTpl.frequency;
				grControl.period = grLayerControlTpl.period;
				grControl.period_unit = grLayerControlTpl.period_unit;
				
				var control_sys_id = grControl.insert();  // inserted control sys_id
				
				var grControlInsert = new GlideRecord('x_anbib_grc_control');
				grControlInsert.get(control_sys_id);      // inserted control record
				
				var wf = new global.Workflow();
				var thisEntityFlows = wf.getRunningFlows(current); // Opened flows for entity
				var thisContext = "";
				while(thisEntityFlows.next()) {
					thisContext = thisEntityFlows.getUniqueValue();
				}
				// Parametros para subflow
				var inputs = {					
					u_fatherwkf: thisContext,
					u_record: current.sys_id.toString()
				};
				var wf_sys_id = wf.startFlow(gs.getProperty("x_anbib_grc.workflow_control"), grControlInsert, "insert", inputs);
				
				workflow.scratchpad.children.push({
					record: control_sys_id,
					childWKF: wf_sys_id,
					status: "running"
				});
			} else {
				if (!grControl.active) {
					grControl.active = true;
					grControl.state = "1";  // review
					grControl.update();
				}
			}
		}
	}
}

