answer = ifScript();

function ifScript() {
	var finished = true;
	for (var i=0; i<workflow.scratchpad.children.length; i++) {
		if (workflow.scratchpad.children[i].status == "running") {
			finished = false;
		}
	}
	if (finished) {
		return 'yes';
	}
	return 'no';
}

