for (var i=0; i<workflow.scratchpad.children.length; i++) {
	var child = workflow.scratchpad.children[i];
	var grControl = new GlideRecord('x_anbib_grc_control');
	grControl.get(child.record);
	
	var contexts = wf.getRunningFlows(grControl);
	if (!contexts.next()) {
		workflow.scratchpad.children[i].status = "finished";
	}
}

