sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var grEvidenceControl = new GlideRecord('x_anbib_grc_m2m_evidence_control');
	grEvidenceControl.addQuery("sop_evidence", current.sys_id.toString());
	grEvidenceControl.query();
	var controls = "";

	while (grEvidenceControl.next()) {
		controls += controls ? ", " + grEvidenceControl.control.getDisplayValue() : grEvidenceControl.control.getDisplayValue();
	}
	template.print(controls);

})(current, template, email, email_action, event);
