var wf = new global.Workflow();

// Consulta action plan de controle e cancela workflow se houver pendente
var grActionPlan = new GlideRecord('x_anbib_grc_action_plan_of_control_inplementation');
grActionPlan.addQuery("control", current.sys_id.toString());
grActionPlan.query();

while (grActionPlan.next()) {
	grActionPlan.active = "false";
	grActionPlan.state = "13";
	grActionPlan.update();
	
	var actionPlanFlows = wf.getRunningFlows(grActionPlan);
	while (actionPlanFlows.next()) {
		var grContextActionPlan = new GlideRecord('wf_context');

		if (grContextActionPlan.get(actionPlanFlows.getUniqueValue()))
			wf.cancelContext(grContextActionPlan);
	}
}

// Consulta evidencias de controle e cancela workflow se houver pendente
var grEvidenceControl = new GlideRecord('x_anbib_grc_m2m_sop_evidences_controls');
grEvidenceControl.addQuery("control", current.sys_id.toString());
grEvidenceControl.query();

while (grEvidenceControl.next()) {
	var evidence = grEvidenceControl.sop_evidence.toString();
	var grEvidence = new GlideRecord('x_anbib_grc_sop_evidence');
    if (grEvidence.get(evidence)) {		
		grEvidence.active = "false";
		grEvidence.state = "-1";
		grEvidence.update();
		
		var evidenceFlows = wf.getRunningFlows(grEvidence); // Opened flows for evidence
		while (evidenceFlows.next()) {
			var grContextEvidence = new GlideRecord('wf_context');

			if (grContextEvidence.get(evidenceFlows.getUniqueValue()))
				wf.cancelContext(grContextEvidence);
		}
	}
}

// Consulta sop de controle e cancela workflow se houver pendente
var grSOPControl = new GlideRecord('x_anbib_grc_m2m_controls_sops');
grSOPControl.addQuery("control", current.sys_id.toString());
grSOPControl.query();

while (grSOPControl.next()) {
	var sop = grSOPControl.sop.toString();
	var grSOP = new GlideRecord('x_anbib_grc_sop');
    if (grSOP.get(sop)) {
		grSOP.active = "false";
		grSOP.state = "-1";
		grSOP.update();
		
		var sopFlows = wf.getRunningFlows(grSOP); // Opened flows for sop		
		while (sopFlows.next()) {
			var grContextSOP = new GlideRecord('wf_context');

			if (grContextSOP.get(sopFlows.getUniqueValue()))
				wf.cancelContext(grContextSOP);
		}
	}
}

current.restart_workflow = false;

