if (current.entity.hosting_type.toString() == "saas" && current.control.control_type_saas.toString() == "true") {
	var grSOPControl = new GlideRecord('x_anbib_grc_m2m_controls_sops');
	grSOPControl.addQuery("control.entity", current.entity.toString());
	grSOPControl.addQuery("control.control.control_type_saas", "true");
	grSOPControl.query();
	
	if (grSOPControl.next()) {
		var sop = grSOPControl.sop.sys_id.toString();
		workflow.scratchpad.sop_workflow = grSOPControl.sop.state.toString();
		
		var grSOPControlInsert = new GlideRecord('x_anbib_grc_m2m_controls_sops');
		grSOPControlInsert.initialize();
		grSOPControlInsert.control = current.sys_id.toString();
		grSOPControlInsert.sop = sop;
		var sopControl = grSOPControlInsert.insert();
	} else {
		startWorkflow();
	}
} else {
	startWorkflow();
}

