if (type && workflow.variables.u_old_type && type != workflow.variables.u_old_type) {
	var grOldControl = new GlideRecord("x_anbib_grc_control");
	grOldControl.addQuery("entity", entity);
	grOldControl.addQuery("layer.entity_type", workflow.variables.u_old_type);
	grOldControl.state = "80";  // disabled - type changed
	grOldControl.active = false;
	grOldControl.updateMultiple();
}

