function ifScript() {
	var grSOPControl = new GlideRecord("x_anbib_grc_m2m_sop_control");
	grSOPControl.addQuery("sop", current.sys_id);
	grSOPControl.query();
	if (grSOPControl.next()) {
		if ((grSOPControl.control.entity.hosting_type.toString() == "SaaS" || grSOPControl.control.entity.hosting_type.toString() == "IaaS" || grSOPControl.control.entity.hosting_type.toString() == "PaaS") && grSOPControl.control.state == 60) {
			return 'yes';
		}
	}
	return 'no';
}

