var grSOPControl = new GlideRecord('x_anbib_grc_m2m_sop_control');
grSOPControl.addQuery("sop", current.sys_id.toString());
grSOPControl.query();

var wf = new global.Workflow();
while (grSOPControl.next()) {
	var grControl = new GlideRecord('x_anbib_grc_control');
	grControl.get(grSOPControl.control.toString());
	
	var contexts = wf.getRunningFlows(grControl);
	if (contexts.next()) {
		wf.broadcastEvent(contexts.sys_id.toString(), "SOP_FINISH");
	}
}

