var grActionPlan = new GlideRecord('x_anbib_grc_action_plan_of_control_inplementation');
grActionPlan.initialize();
grActionPlan.control = current.sys_id.toString();
grActionPlan.state = "1";
var actionPlan = grActionPlan.insert();  // inserted sys_id

var grActionPlanInsert = new GlideRecord('x_anbib_grc_action_plan_of_control_inplementation');
grActionPlanInsert.get(actionPlan);      // inserted record

var wf = new global.Workflow();
var thisControlFlows = wf.getRunningFlows(current); // Opened flows for entity
var thisContext = "";
while(thisControlFlows.next()) {
	thisContext = thisControlFlows.getUniqueValue();
}
// Parametros para subflow
var inputs = {					
	u_fatherwkf: thisContext,
	u_record: current.sys_id.toString()
};
var wf_sys_id = wf.startFlow(gs.getProperty("x_anbib_grc.sys_id.workflow.action_plan_control_implementation"), grActionPlanInsert, "insert", inputs);

workflow.scratchpad.child = {
	record: actionPlan,
	childWKF: wf_sys_id,
	status: "running"
};

