var sopResponsible = current.entity.itc_responsible;
// Se for SaaS e o template possuir flag de coberto por SOC2 report, abre somente um processo para aplicao
var hostingType = current.entity.hosting_type.toString();
if ((hostingType == "SaaS" || hostingType == "IaaS" || hostingType == "PaaS") && current.layer.layer_type.toString() == "2" && current.expected_state.toString() == "2") {
	var grSOPControl = new GlideRecord('x_anbib_grc_m2m_sop_control');
	grSOPControl.addQuery("control.entity", current.entity.toString());
	grSOPControl.addQuery("control.layer.layer_type", "2");
	grSOPControl.query();
	
	if (grSOPControl.next()) {
		var sop = grSOPControl.sop.sys_id.toString();
		workflow.scratchpad.sop_workflow = grSOPControl.sop.state.toString();
		
		var grSOPControlInsert = new GlideRecord('x_anbib_grc_m2m_sop_control');
		grSOPControlInsert.initialize();
		grSOPControlInsert.control = current.sys_id.toString();
		grSOPControlInsert.sop = sop;
		var sopControl = grSOPControlInsert.insert();
	} else {
		startWorkflow(sopResponsible);
	}
} else {
	startWorkflow(sopResponsible);
}

