	var grEvidenceInsert = new GlideRecord('x_anbib_grc_sop_evidence');
	grEvidenceInsert.get(evidence);      // inserted record

	var wf = new global.Workflow();
	var thisControlFlows = wf.getRunningFlows(current); // Opened flows for entity
	var thisContext = "";
	while(thisControlFlows.next()) {
		thisContext = thisControlFlows.getUniqueValue();
	}
	// Parametros para subflow
	var inputs = {					
		u_fatherwkf: thisContext,
		u_record: current.sys_id.toString()
	};
	var wf_sys_id = wf.startFlow(gs.getProperty("x_anbib_grc.sys_id.workflow.sop_evidence"), grEvidenceInsert, "insert", inputs);

// 	workflow.scratchpad.child = {
// 		record: evidence,
// 		childWKF: wf_sys_id,
// 		status: "running"
// 	};
	
}

